import React from "react";

interface Props {
    titles: string[],
    descriptors: string[][],
    illustrations: string[],
    prefix: string
}


// Put arrow on selected tab =)
class Tabs extends React.PureComponent<Props>{
    rendered_page_index = 0;

    openProject = (id: number) => {
        this.rendered_page_index = id;
        this.forceUpdate();
    }

    render() {
        const { titles, descriptors, illustrations, prefix } = this.props;
        const openProject = this.openProject.bind(this);
        let tabs = [];
        let pages = [];
        let rendered_page;

        for (let i = 0; i < titles.length; i++) {
            tabs.push(
                <button className={prefix + "Tabs"} onClick={() => { openProject(i) }}>{(titles[i].split(" ").map(word=>word.substr(0,3)).join(" "))}</button>
            );
        }


        for (let i = 0; i < titles.length; i++) {
            pages.push(
                <div id={prefix + titles + i} className="tabContent">
                    <div className="tabDescriptor">
                        <h3>{titles[i]}</h3>
                        {descriptors[i].map(paragraph => <p>{paragraph}</p>)}
                    </div>
                    <div className="tabDisplay">
                        <img src={illustrations[i]} alt="Illustrative Image" />
                    </div>
                </div>
            );
        }

        rendered_page = pages[this.rendered_page_index];

        return (
            <div id={prefix + "Window"} className="tabs_and_content">
                <div id={prefix + "Tabs"} className="tabs" style={{gridTemplateColumns: "repeat("+titles.length+",minmax(min-content,max-content))"}}>
                    {tabs}
                </div>
                <div id={prefix + "Content"} className="Content">
                    {rendered_page}
                </div>
            </div>
        );
    }
}


export default Tabs
