import React from "react";

interface Props {
    one_liners: string[],
    expansions: string[],
    books: string[],
}

class Introduction extends React.PureComponent<Props>{

    // Remove the data-aos if not using AOS on App root.
    render() {
        const { one_liners, expansions, books } = this.props;
        var traits = [];
        var shelves = [];

        // Short bio
        for (let i = 0; i < one_liners.length; i++) {
            traits.push(
                <p className="text_expanded">
                    <span className="expand_text">{one_liners[i]}</span> {expansions[i]}
                </p>
            );
        };

        // Library
        for (let i = 0; i < books.length / 5; i++) {
            let shelf = [];
            for (let j = 0; j < 5; j++) {
                // Add books to shelf
                shelf.push(
                    <div className="book" style={{ backgroundImage: "url(" + books[i * 5 + j] + ")" }}></div>
                );
            }
            // Add shelf to shelves
            shelves.push(
                <div className="bookShelf">
                    <div className="bookRow">
                        {shelf}
                    </div>
                    <div className="shelf"></div>
                </div>
            );
        };


        return (
            <section id="introduction">
                <h1>if isPerson(<i>you</i>) then:</h1>
                <p className="text_expanded">
                    <span className="expand_text">Hi! My name is Santiago and I was born and raised in Mexico</span>,
                        my mother got my sister and I the privilege of going to fancy schools and learning english.
                        I then got the chance of going on to learn french at another - even fancier - place,
                        and getting several scholarships to study what I love most in the lovely maple land of Canada.
                        <br /><br />
                        I'm trying to become reliable and competent, to be able to give back to the people that have pushed
                        me forward.
                </p>
                <p className="text_expanded">
                    <span className="expand_text">I'm currently</span> a second year student and teacher assistant at the
                        University of Montreal, in the Department of Computer Science and Operational Research (DIRO).
                        It's been a bit odd to start my studies during the pandemic, but the unique challenges that came
                        with that have taught me things I wouldn't have expected.
                </p>
                <p className="text_expanded">
                    <span className="expand_text">I love</span> playing the electric guitar; reading - especially topics outside
                        of my field; I have some weird obsession with water and all its sports,
                        specially those I'm terrible at. Like rowing with the UdeM's team, it's pretty fun to scream
                        synchronously with a bunch of charismatic strangers while desperately trying not to tip over.<br />
                        Also winning - winning is fun and a good excuse to scream some more.
                </p>
                <p className="text_expanded">
                    <span className="expand_text">I really like teaching</span> and communicating science;
                        I've met all kinds of people throughout my life and our different tastes,
                        talents, experience, backgrounds, handicaps and challenges have taught me
                        more than I could ever repay. I feel like by teaching, I pay just a bit of that debt.
                </p>
                <span className="expand_text" style={{color: "var(--metallic-blue)"}}>
                    I'll tell you what I read
                </span>
                <div id="library">
                    <div id="libHeader">
                        You can tell who I am
                    </div>
                    <div className="bookShelfs">
                        {shelves}
                    </div>
                </div>
            </section>
        );
    }
}

export default Introduction;
