import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';


// Import 
import content from "./content.json"


// Components
import Navbar from "./Components/Navbar.tsx"
import Header from "./Components/Header.tsx"
import Education from "./Components/Education.tsx"
import Experience from "./Components/Experience.tsx"
import Introduction from "./Components/Introduction.tsx"

function _extractImages(_content){
  // Of form: [key, values]. Here the values are arrays of paths.
  let image_sections = Object.entries(content.images);
  let extracted_pairs = {};
  for(const image_section of image_sections){
    let [section,images] = image_section;
    let extracted_images = [];
    // Take every path of image and format it as proper objects for input.
    for(const image of images){
      extracted_images.push(require(image).default);
    }
    extracted_pairs.set(section,extracted_images);
  }
  return extracted_pairs;
}

function App() {
  /* ------ Content ------- */
  const images = content.images;

  // Nav
  const sections = ["introduction", "education", "experience", "projects", "interests", "accolades"];
  const languages = ["English", "Français", "Español"];

  // Schools
  const school_images = images.school;
  const school_titles = ["Université de Montréal", "Lycée franco-mexicain"];
  const school_descriptors = ["Current average of 3.75/4.3 (A-)", "Mention très bien: TB (18.25/20)"];
  const degrees = ["Bachelors of Science, Computer Science", "French Baccalaureat, Engineering Science"];
  const time_n_places = ["Montreal | 2020-2023 (Expected)", "Mexico | 2017-2020"];
  const shorthands = ["UdeM", "LFM"];

  // Experience
  const work_titles = ["Full Stack Developer - intern", "Programming Languages and Paradigms - teacher assistant",
    "Intro to programming - teacher assistant", "Discrete structures for computer science - teacher assistant"];
  const work_images = images.work;
  const work_descriptors = [
    `Implemented experimental features, documented and debugged on Ixiasoft's DITA CCMS 
    web application for technical documentation.
    Mainly worked with Java, TypeScript, React (by itself and with Redux), and Apache. 
    Also handled -to a lesser extent- Maven and CSS.
    `,
    `Under the supervision of titular professor Stefan Monnier I worked with another BSc student to
    teach and grade a class of 50 on the concepts and paradigms that programming languages mainly use/belong to
    - through Haskell, Lisp (mainly Elisp), Prolog, C and Java.
    `,
    `Under the supervision of titular professor Marc Feeley and
  lecturer Olivier Melançon I worked alongside 7 other students to teach and grade a class of
  almost 300 on the basics of programming - through python
  `,
    `Under the supervision of titular professor Miklós Csűrös I worked with a Masters student to
  teach and grade a class of 100 on the discrete structures from mathematics that we most
  commonly use as computer scientists.
  `
  ];

  /* ------ Form ------- */
  return (
    <div className="App">
      <Navbar sections={sections} />
      <main>
        <Header icon={images.icons.header} languages={languages} />
        <Intro />
