import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';


// Import 
import content from "./content.json"


// Components
import Navbar from "./Components/Navbar.tsx"
import Header from "./Components/Header.tsx"
import Education from "./Components/HomeSections/Education.tsx"
import Experience from "./Components/HomeSections/Experience.tsx"
import Introduction from "./Components/HomeSections/Introduction.tsx"

function _extractImages(_content) {
  // Of form: [key, values]. Here the values are arrays of paths.
  let image_sections = Object.entries(content.images);
  let extracted_pairs = {};
  for (const image_section of image_sections) {
    let [section, images] = image_section;
    let extracted_images = [];
    // Take every path of image and format it as proper objects for input.
    for (const image of images) {
      extracted_images.push(require(image).default);
    }
    extracted_pairs.set(section, extracted_images);
  }
  return extracted_pairs;
}

function App() {
  /* ------ Content ------- */
  // Nav
  const nav = content.nav;
  const sections = nav.sections;
  const languages = nav.languages;
  const icon = nav.icon;

  // Intro
  const intro = content.introduction;
  const intro_hooks = intro.one_liners;
  const intro_text = intro.paragraphs;
  const books = intro.book_covers;

  // Schools
  const school = content.school;
  const school_images = school.images;
  const school_titles = school.titles;
  const school_descriptors = school.descriptors;
  const degrees = school.degrees;
  const time_n_places = school.details;
  const shorthands = school.abreviations;

  // Experience
  const work = content.work;
  const work_titles = work.titles;
  const work_images = work.images;
  const work_descriptors = work.descriptors;

  /* ------ Form ------- */
  return (
    <div className="App">
      <Navbar sections={sections} />
      <main>
        <Header icon={icon} languages={languages} />
        <Introduction one_liners={intro_hooks} expansions={intro_text} books={books} />
        <Education images={school_images} shorthands={shorthands} titles={school_titles}
          degrees={degrees} times_and_places={time_n_places} descriptors={school_descriptors} />
        <Experience titles={work_titles} images={work_images} descriptors={work_descriptors} />
        <Projects />
      "IndependentResearch"
      "Opinions"
      <Interests />
        <Accolades />
      </main>
    </div>
  );
}

const Projects = () => {
  return (
    <section id="projects">
      <h1>Projects:</h1>
      <div id="projTab" className="tab">
        <button id="defaultProject" className="projectLinks">Gesture
                recognition</button>
        <button className="projectLinks">Vaccination management </button>
        <button className="projectLinks">21st century abacus</button>
        <button className="projectLinks">Interpreters</button>
        <button className="projectLinks">Environnemental
                Simulation</button>
        <button className="projectLinks">This site!</button>
      </div>
      <div id="gesture" className="tabProjects tabcontent">
        <div className="projectDescriptor">
          <h3>Gesture recognition web application (Flask and tensorflow)</h3>
          <p>Developed during a 2 month period with a team of 3. You can check it out <a target="_blank"
            href="https://github.com/Gfecito/GestureRecon-app" rel="noreferrer">here</a><br />
                    My teammates trained the model while I learned Flask and made a web application, front to
                    back<br />
                    It remains unfinished, some animations are missing and files can't yet be uploaded, but I'll
                    come back to it with more experience.</p>
        </div>
        <div className="projectDisplay">
          <div className="mySlides slides2">
            <img src="Images/ProjectThumbnails/AIThumbnail.png" alt="AI Thumbnail 1" />
          </div>
          <div className="mySlides slides2">
            <img src="Images/ProjectThumbnails/CHandThumbnail.png" alt="AI Thumbnail 2" />
          </div>
          <div className="mySlides slides2">
            <img src="Images/ProjectThumbnails/CThumbnail.png" alt="AI Thumbnail 3" />
          </div>
          <div className="mySlides slides2">
            <img src="Images/ProjectThumbnails/PalmThumbnail.png" alt="AI Thumbnail 4" />
          </div>
        </div>
      </div>
      <div id="vaccine" className="tabProjects tabcontent">
        <div className="projectDescriptor">
          <h3>Vaccination management system</h3>
          <p>
            A functioning app developped with Java that includes a graphical interface and a data management
                    system. <br />
                    I had a hand in the planning and design of the project, and wrote around 95% of the 2000 lines
                    of code in it, plus documentation. <br />
                    As requested by the teaching staff of that class, the code is private.<br />
                    Currently on its last stage of development.
                </p>
        </div>
        <div className="projectDisplay">
          <img src="Images/ProjectThumbnails/Vaccination.PNG" alt="Illustrative Image" />
        </div>
      </div>
      <div id="abacus" className="tabProjects tabcontent">
        <div className="projectDescriptor">
          <h3>Counters</h3>
          <p>School project for object oriented programming with java; its a small window:<br /><br />

                    Using JavaFX, we (2 people) made a fancy window with 2 counters: you can add and substract,
                    divide and <br />
                    multiply, undo and redo, and export a .txt file with all registered operations <br />
                    You can
                    <a target="_blank"
              href="https://docs.google.com/uc?export=download&amp;id=1C0trEs0PgjXB1vcU6hALwtBdeMufHwef">download</a>
                    the .zip of the project or you can see it on
                    <a target="_blank" href="https://github.com/Gfecito/JavaFX-Counter">Github</a><br />
                    Warning: it is in french
                </p>
        </div>
        <div className="projectDisplay">
          <img src="Images/ProjectThumbnails/CounterThumbnail.png" alt="Counter App Thumbnail" />
        </div>
      </div>
      <div id="interpreters" className="tabProjects tabcontent">
        <div className="projectDescriptor">
          <h3>Pseudo-Lisp interpreter made with Haskell</h3>
          <p>
            Built the interpretation and evaluation section of a Haskell program that could read and execute
            files from a Lisp inspired language. Dealt with functions, closure, environments, variables,
            etc... the whole enchilada.
                </p>
        </div>
        <div className="projectDescriptor">
          <h3>Pseudo-Haskell interpreter made with Prolog</h3>
          <p>
            Same thing, but this time it was a Haskell-inspired language and using Prolog for reading and
                    executing.<br />
                    Out of respect of the professor's code that compiled before our interpreter played any role, the
                    code is private.<br />
                    Currently on its last stage of development.
                </p>
        </div>
        <div className="projectDisplay">
          <img src="Images/ProjectThumbnails/interpreter.png" alt="Illustrative Image" />
        </div>
      </div>
      <div id="simulation" className="tabProjects tabcontent">
        <div className="projectDisplay">
          <img src="Images/ProjectThumbnails/WildLionSimulator3D.jpeg" alt="Illustrative Image" />
        </div>
        <div className="projectDescriptor">
          <h3>OOP Environment simulation made with Java</h3>
          <p>
            <span style={{ color: "green", animation: "none", fontSize: "medium" }}>
              <span style={{ color: "red", animation: "none", fontSize: "medium" }}>DISCLAIMER:</span> The image
                        presented is not from the project (the project only prints to the terminal). <br />
                        It is merely illustrative and from a game called "Wild Lion Simulator 3D". </span><br /><br />
                    Using objected oriented programming we made an environmental simulation program.<br />
